$(function() {
  $('.header__account-language').on('click', () => {
    $('.header__account-language-items').slideToggle(300)
  })
  $('.popup').magnificPopup({
    type: 'inline',
    removalDelay: 500, //delay removal by X to allow out-animation
    callbacks: {
        beforeOpen: function () {
            this.st.mainClass = this.st.el.attr('data-effect');
        }
    },
    midClick: true // allow opening popup on middle mouse click. Always set it to true if you don't provide alternative source.
  });
  $('.header__btn').on('click', () => {
    $('.header__btn').toggleClass('open')
    $('.over').toggleClass('open')
    $('.header__mobile').toggleClass('open')
  })
  $('.header__list-link').on('click', () => {
    $('.header__btn').removeClass('open')
    $('.over').removeClass('open')
    $('.header__mobile').removeClass('open')
  })
  $('.over').on('click', () => {
    $('.header__btn').removeClass('open')
    $('.over').removeClass('open')
    $('.header__mobile').removeClass('open')
  })
  $('.header__account-button').on('click', () => {
    $('.header__btn').removeClass('open')
    $('.over').removeClass('open')
    $('.header__mobile').removeClass('open')
  })
  
  mixitup('.price__inner', {
    load: {
      filter: '.yearly'
    }
  })
})

window.onscroll = function showHeader () {
  console.log(window.pageYOffset);
  const header = document.querySelector('.header');
  if (window.pageYOffset > 100) {
    header.classList.add('active');
  } else {
    header.classList.remove('active');
  }
}
